sqlMap.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT A.REQST_PROCESS_ID reqstProcessId , A.REQST_PROCESS_SN reqstProcessSn , (SELECT X.MBER_NM FROM OE1TNUSERINFO X WHERE B.APPLCNT_ID = X.MBER_ID) applcntNm , (SELECT X.CODE_NM FROM OE1TNOECOMMDETAILCD X WHERE X.CODE_ID = 'OE1103' AND X.CODE = B.JOB_SE_CODE) jobSeCode , (SELECT X.CODE_NM FROM OE1TNOECOMMDETAILCD X WHERE X.CODE_ID = 'OE1104' AND X.CODE = B.PROCESS_STTUS_CODE) processSttusCode , B.REQST_DE reqstDe , A.DTA_DICARY_NM dtaDicaryNm , A.DTA_DICARY_ENG_NM dtaDicaryEngNm , A.DOMN_NM domnNm , A.DATA_TY dataTy , A.DATA_LT dataLt FROM OE1TNDATADICREQUESTINFO A , OE1TNMETADATAREQUESTINFO B WHERE A.REQST_PROCESS_ID = B.REQST_PROCESS_ID AND A.REQST_PROCESS_SN = B.REQST_PROCESS_SN AND A.REQST_PROCESS_SN = (SELECT MAX(REQST_PROCESS_SN) FROM OE1TNDATADICREQUESTINFO X WHERE X.REQST_PROCESS_ID = A.REQST_PROCESS_ID) AND A.DTA_DICARY_NM LIKE CONCAT(CONCAT('%', #srchDtaDicaryNm#), '%')
(SELECT MBER_NM FROM OE1TNUSERINFO X WHERE B.APPLCNT_ID = X.MBER_ID) LIKE CONCAT(CONCAT('%',#srchApplcntNm#),'%')
PROCESS_STTUS_CODE = #srchProcessSttusCode#
ORDER BY A.REQST_PROCESS_ID DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#
