sqlMap.select:
SELECT DTA_DICARY_ID dtaDicaryId , DTA_DICARY_NM dtaDicaryNm , DTA_DICARY_ENG_NM dtaDicaryEngNm , DOMN_NM domnNm , DATA_TY dataTy , DATA_LT dataLt FROM OE1TNDATADIC A WHERE A.DTA_DICARY_ID = ( SELECT MAX(DTA_DICARY_ID) DTA_DICARY_ID FROM OE1TNDATADIC WHERE DTA_DICARY_NM = #dtaDicaryNm#
DTA_DICARY_ID != #dtaDicaryId#
)

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT DTA_DICARY_ID dtaDicaryId , DTA_DICARY_NM dtaDicaryNm , DTA_DICARY_ENG_NM dtaDicaryEngNm , DOMN_NM domnNm , DATA_TY dataTy , DATA_LT dataLt , USE_AT useAt , TO_CHAR(LAST_UPDT_PNTTM,'YYYY-MM-DD') lastUpdtPnttm , LAST_UPDUSR_ID lastUpdusrId , (SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) lastUpdusrNm FROM OE1TNDATADIC A WHERE DTA_DICARY_NM LIKE CONCAT(CONCAT('%', #srchDtaDicaryNm#), '%')
USE_AT = #srchUseAt#
(SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) LIKE CONCAT(CONCAT('%',#srchLastUpdusrNm#),'%')
ORDER BY LAST_UPDT_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#
