sqlMap.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT DOMN_CL_ID domnClId , DOMN_CL_NM domnClNm , USE_AT useAt , TO_CHAR(LAST_UPDT_PNTTM,'YYYY-MM-DD') lastUpdtPnttm , LAST_UPDUSR_ID lastUpdusrId , (SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) lastUpdusrNm FROM OE1TNDOMAINCLASSINFO A WHERE DOMN_CL_NM LIKE CONCAT(CONCAT('%', #srchDomnClNm#), '%')
USE_AT = #srchUseAt#
(SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) LIKE CONCAT(CONCAT('%',#srchLastUpdusrNm#),'%')
DOMN_CL_ID = #srchDomnClId#
ORDER BY LAST_UPDT_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#
