sqlMap.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT REQST_PROCESS_ID reqstProcessId , REQST_PROCESS_SN reqstProcessSn , (SELECT X.CODE_NM FROM OE1TNOECOMMDETAILCD X WHERE X.CODE_ID = 'OE1103' AND X.CODE = A.JOB_SE_CODE) jobSe , REQST_DE reqstDe , (SELECT X.MBER_NM FROM OE1TNUSERINFO X WHERE A.APPLCNT_ID = X.MBER_ID) applcntNm , REQST_PRVONSH reqstPrvonsh , (SELECT X.CODE_NM FROM OE1TNOECOMMDETAILCD X WHERE X.CODE_ID = 'OE1104' AND X.CODE = A.PROCESS_STTUS_CODE) processSttus , PROCESS_DE processDe , (SELECT X.MBER_NM FROM OE1TNUSERINFO X WHERE A.OPETR_ID = X.MBER_ID) opetrNm , PROCESS_PRVONSH processPrvonsh FROM OE1TNMETADATAREQUESTINFO A WHERE PROCESS_STTUS_CODE != '01'
JOB_SE_CODE = #srchJobSeCode#
(SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.APPLCNT_ID = X.MBER_ID) LIKE CONCAT(CONCAT('%',#srchApplcntNm#),'%')
PROCESS_STTUS_CODE = #srchProcessSttusCode#
REQST_DE BETWEEN #srchReqstDeStart# AND #srchReqstDeEnd#
ORDER BY A.REQST_PROCESS_ID DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#
