sqlMap.select:
SELECT WORD_ENG_ABRV_NM wordEngAbrvNm , (SELECT WORD_NM FROM OE1TNWORDDIC X WHERE A.WORD_ENG_ABRV_NM = X.WORD_ENG_ABRV_NM) wordNm , SYNONM_IDNTFC_NO synonmIdntfcNo , SYNONM_NM synonmNm , USE_AT useAt FROM OE1TNSYNONYMINFO A WHERE WORD_ENG_ABRV_NM = #wordEngAbrvNm# AND SYNONM_IDNTFC_NO = (SELECT MAX(SYNONM_IDNTFC_NO) SYNONM_IDNTFC_NO FROM OE1TNSYNONYMINFO WHERE WORD_ENG_ABRV_NM = #wordEngAbrvNm# AND SYNONM_NM = #synonmNm#
SYNONM_IDNTFC_NO != #synonmIdntfcNo#
)
