sqlMap.select:
SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT WORD_ENG_ABRV_NM wordEngAbrvNm , WORD_NM wordNm , WORD_ENG_NM wordEngNm , WORD_DC wordDc , USE_AT useAt , TO_CHAR(LAST_UPDT_PNTTM,'YYYY-MM-DD') lastUpdtPnttm , LAST_UPDUSR_ID lastUpdusrId , (SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) lastUpdusrNm FROM OE1TNWORDDIC A WHERE WORD_NM LIKE CONCAT(CONCAT('%', #srchWordNm#), '%')
USE_AT = #srchUseAt#
(SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) LIKE CONCAT(CONCAT('%',#srchLastUpdusrNm#),'%')
WORD_ENG_ABRV_NM = #srchWordEngAbrvNm#
ORDER BY LAST_UPDT_PNTTM DESC
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage#

SELECT * FROM ( SELECT ROWNUM RNUM, ALL_LIST.* FROM (
/* 구현 Sql */
SELECT WORD_ENG_ABRV_NM wordEngAbrvNm , WORD_NM wordNm , '' synonmNm , WORD_ENG_NM wordEngNm , TO_CHAR(LAST_UPDT_PNTTM,'YYYY-MM-DD') lastUpdtPnttm , LAST_UPDUSR_ID lastUpdusrId , (SELECT MBER_NM FROM OE1TNUSERINFO X WHERE A.LAST_UPDUSR_ID = X.MBER_ID) lastUpdusrNm FROM OE1TNWORDDIC A WHERE WORD_NM LIKE CONCAT(CONCAT('%', #srchWordNm#), '%') AND USE_AT = 'Y' UNION ALL SELECT A.WORD_ENG_ABRV_NM wordEngAbrvNm , A.WORD_NM wordNm , B.SYNONM_NM synonmNm , A.WORD_ENG_NM wordEngNm , TO_CHAR(B.LAST_UPDT_PNTTM,'YYYY-MM-DD') lastUpdtPnttm , B.LAST_UPDUSR_ID lastUpdusrId , (SELECT MBER_NM FROM OE1TNUSERINFO X WHERE B.LAST_UPDUSR_ID = X.MBER_ID) lastUpdusrNm FROM OE1TNWORDDIC A , OE1TNSYNONYMINFO B WHERE B.SYNONM_NM LIKE CONCAT(CONCAT('%', #srchWordNm#), '%') AND A.WORD_ENG_ABRV_NM = B.WORD_ENG_ABRV_NM AND A.USE_AT = 'Y' AND B.USE_AT = 'Y'
/* 구현 Sql */
) ALL_LIST ) WHERE RNUM > #firstIndex# AND RNUM <= #firstIndex# + #recordCountPerPage# ORDER BY wordNm
