sqlMap.select:
select sum(T7.REGIST_COUNT) as REGIST_COUNT ,sum(T7.WORKING_COUNT) as WORKING_COUNT ,sum(T7.COMPT_COUNT) as COMPT_COUNT ,sum(T7.COMPT_IN_END_REQ) as COMPT_IN_END_REQ ,sum(T7.COMPT_OVER_END_REQ) as COMPT_OVER_END_REQ ,sum(T7.COMPT_IN_PLAN) as COMPT_IN_PLAN ,sum(T7.COMPT_OVER_PLAN) as COMPT_OVER_PLAN ,T7.DIVISION as DIVISION , T7.order_id as order_id from ( select (case when b.CHANGE_PROCESS_STTUS_CODE > '01' then 1 else 0 end) as REGIST_COUNT , (case when b.CHANGE_PROCESS_STTUS_CODE != '01' and b.CHANGE_PROCESS_STTUS_CODE != '02' and b.CHANGE_PROCESS_STTUS_CODE != '07 ' then 1 else 0 end) as WORKING_COUNT , (case b.CHANGE_PROCESS_STTUS_CODE when '07' then 1 else 0 end) as COMPT_COUNT , (case when a.COMPT_REQUST_DE >= b.REAL_OPERT_END_DE and b.CHANGE_PROCESS_STTUS_CODE = '07' then 1 else 0 end) as COMPT_IN_END_REQ , (case when b.REAL_OPERT_END_DE > a.COMPT_REQUST_DE and b.CHANGE_PROCESS_STTUS_CODE = '07' then 1 else 0 end) as COMPT_OVER_END_REQ , (case when b.PLAN_END_DE >= b.REAL_OPERT_END_DE and b.CHANGE_PROCESS_STTUS_CODE = '07' then 1 else 0 end) as COMPT_IN_PLAN , (case when b.REAL_OPERT_END_DE > b.PLAN_END_DE and b.CHANGE_PROCESS_STTUS_CODE = '07' then 1 else 0 end) as COMPT_OVER_PLAN , (case cast(#statusCondition# as varchar(1) ) when '1' then substr(a.CHANGE_REQUST_DE,1,4) || '-' || substr(a.CHANGE_REQUST_DE,5,2) when '2' then T1.code_nm when '3' then T2.code_nm when '4' then T3.code_nm when '5' then T4.code_nm when '6' then T5.mber_nm when '7' then T6.code_nm when '8'then T7.code_nm end ) as DIVISION , (case cast(#statusCondition# as varchar(1) ) when '1' then substr(a.CHANGE_REQUST_DE,1,6) when '2' then T1.code when '3' then T2.code when '4' then T3.code when '5' then T4.code when '6' then T5.mber_nm when '7' then T6.code when '8'then T7.code end ) as order_id from OE1TNCHANGEREQUSTINFO a left outer join OE1TNCHANGEPROCINFO b on a.CHANGE_REQUST_PROCESS_ID = b.CHANGE_REQUST_PROCESS_ID LEFT OUTER JOIN OE1TNOECOMMDETAILCD T1 on T1.CODE_ID = 'OE1016' AND b.CHANGE_SE_CODE = T1.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T2 on T2.CODE_ID = 'OE1005' AND b.EMRGNCY_PROCESS_AT = T2.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T3 on T3.CODE_ID = 'OE1017' AND b.CHANGE_AFFC_SE_CODE = T3.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T4 on T4.CODE_ID = 'OE1018' AND b.CHANGE_SCOPE_SE_CODE = T4.CODE LEFT OUTER JOIN OE1TNUSERINFO T5 on b.CHANGE_OPERTOR_ID = T5.MBER_ID LEFT OUTER JOIN OE1TNOECOMMDETAILCD T6 on T6.CODE_ID = 'OE1020' AND a.OPER_JOB_SE_CODE = T6.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T7 on T7.CODE_ID = 'OE1002' AND a.CHANGE_REQUST_RESN_CODE = T7.CODE where 1=1
a.CHANGE_REQUST_DE between REPLACE(#fromDate#,'-','') AND REPLACE(#toDate#,'-','')
) T7 where T7.DIVISION is not null group by T7.DIVISION, T7.order_id order by T7.order_id ASC
