sqlMap.select:
select sum(T4.rceptCount) as rceptCount ,sum(T4.processCount) as processCount ,sum(T4.endCount) as endCount ,sum(T4.endCountIn) as endCountIn ,sum(T4.endCountOver) as endCountOver ,T4.division as division ,T4.order_id as order_id from ( select (case when a.REQUST_STTUS_CODE = '02' or a.REQUST_STTUS_CODE = '04' or a.REQUST_STTUS_CODE = '06' then 1 else 0 end) as rceptCount , (case a.REQUST_STTUS_CODE when '04' then 1 else 0 end) as processCount , (case a.REQUST_STTUS_CODE when '06' then 1 else 0 end) as endCount , (case when a.REQUST_STTUS_CODE = '06' and a.COMPT_REQUST_DE >= a.PROCESS_COMPT_DE then 1 else 0 end) as endCountIn , (case when a.REQUST_STTUS_CODE = '06' and a.COMPT_REQUST_DE < a.PROCESS_COMPT_DE then 1 else 0 end) as endCountOver , (case #statusCondition# when '1' then TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYY-MM') when '2' then T1.code_nm when '3' then T2.code_nm when '4' then T3.mber_nm end ) as division , (case #statusCondition# when '1' then TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYY-MM') when '2' then T1.code when '3' then T2.code when '4' then T3.mber_nm end ) as order_id from OE1TNOPERIMPROVREQ a LEFT OUTER JOIN OE1TNOECOMMDETAILCD T1 on T1.CODE_ID = 'OE1020' AND a.OPER_JOB_SE_CODE = T1.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T2 on T2.CODE_ID = 'OE1012' AND a.REQUST_TY_CODE = T2.CODE LEFT OUTER JOIN OE1TNUSERINFO T3 on T3.MBER_ID = a.CHARGER_ID where a.REQUST_STTUS_CODE <> '01' and a.REQUST_STTUS_CODE <> '03' and a.REQUST_STTUS_CODE <> '05' and a.REQUST_STTUS_CODE <> '07'
TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYYMMDD') between REPLACE(#fromDate#,'-','') AND REPLACE(#toDate#,'-','')
) T4 group by T4.division, T4.order_id order by T4.order_id ASC

select sum(t.totRequstCount) as totRequstCount ,sum(t.preRceptCount) as preRceptCount ,sum(t.changeTransCount) as changeTransCount ,sum(t.returnCount) as returnCount from ( select 1 as totRequstCount ,(case when a.REQUST_STTUS_CODE = '01' then 1 else 0 end) as preRceptCount , (case when a.REQUST_STTUS_CODE = '03' or a.REQUST_STTUS_CODE = '05' then 1 else 0 end) as changeTransCount , (case when a.REQUST_STTUS_CODE = '07' then 1 else 0 end) as returnCount from OE1TNOPERIMPROVREQ a WHERE 1=1
TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYYMMDD') BETWEEN REPLACE(#fromDate#,'-','') AND REPLACE(#toDate#,'-','')
) t
