sqlMap.select:
select sum(T4.registCount) as registCount ,sum(T4.preventActCount) as preventActCount ,sum(T4.preventEndCount) as preventEndCount ,sum(T4.riskRemove) as riskRemove ,sum(T4.riskExtinction) as riskExtinction ,T4.division as division ,T4.order_id as order_id from ( select (case when a.RISK_STTUS_CODE >= '01' then 1 else 0 end) as registCount , (case a.RISK_STTUS_CODE when '02' then 1 else 0 end) as preventActCount , (case a.RISK_STTUS_CODE when '03' then 1 else 0 end) as preventEndCount , (case when a.RISK_STTUS_CODE = '03' and a.PREVNT_ACT_RESULT_SE = '01' then 1 else 0 end) as riskRemove , (case when a.RISK_STTUS_CODE = '03' and a.PREVNT_ACT_RESULT_SE = '02' then 1 else 0 end) as riskExtinction , (case cast(#statusCondition# as varchar(1) ) when '1' then TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYY-MM') when '2' then T1.code_nm when '3' then T2.code_nm when '4' then T3.mber_nm end ) as division , (case cast(#statusCondition# as varchar(1) ) when '1' then TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYY-MM') when '2' then T1.code when '3' then T2.code when '4' then T3.mber_nm end ) as order_id from OE1TNRISKMANAGE a LEFT OUTER JOIN OE1TNOECOMMDETAILCD T1 on T1.CODE_ID = 'OE1025' AND a.RISK_TY_CODE = T1.CODE LEFT OUTER JOIN OE1TNOECOMMDETAILCD T2 on T2.CODE_ID = 'OE1022' AND a.DGDGR = T2.CODE LEFT OUTER JOIN OE1TNUSERINFO T3 on T3.MBER_ID = a.CHARGER_ID where 1=1
TO_CHAR(a.FRST_REGISTER_PNTTM, 'YYYYMMDD') between REPLACE(#fromDate#,'-','') AND REPLACE(#toDate#,'-','')
) T4 group by T4.division, T4.order_id order by T4.order_id ASC
